		select * from OrdersDetail od
		INNER JOIN product p on od.pid=p.pid
		INNER JOIN Orders o on od.orderid=o.orderid
		INNER JOIN specification s on od.sid=s.sid


				and od.orderid=#{orderid}



 			limit ${(page-1)*limit},#{limit}

